create database cinema;
use cinema;

create table salas(
idsala int auto_increment primary key,
num_sala varchar(10) not null,
capacidade int(3) not null,
horario varchar(5) not null,
preco int(3) not null, 
disponivel int(3) not null,
vendas int(3) not null
);
 
 create table filmes(
 idfilme int auto_increment primary key,
 titulo varchar(30) not null,
 duracao varchar(6) not null,
 sinopse varchar(1000) not null,
 /*vendas int(3) not null,*/
 id_sala int,
 foreign key (id_sala) references salas(idsala) 
 );
 
 
